import api from './api';

export const auditService = {
  // Templates
  async getTemplates() {
    const response = await api.get('/templates/');
    return response.data;
  },

  async getTemplate(id) {
    const response = await api.get(`/templates/${id}/`);
    return response.data;
  },

  // Audits
  async getAudits(filters = {}) {
    const params = new URLSearchParams(filters).toString();
    const response = await api.get(`/audits/?${params}`);
    return response.data;
  },

  async getAudit(id) {
    const response = await api.get(`/audits/${id}/`);
    return response.data;
  },

  async createAudit(data) {
    const response = await api.post('/audits/', data);
    return response.data;
  },

  async startAudit(id) {
    const response = await api.post(`/audits/${id}/start/`);
    return response.data;
  },

  async completeAudit(id) {
    const response = await api.post(`/audits/${id}/complete/`);
    return response.data;
  },

  async getQuestions(auditId) {
    const response = await api.get(`/audits/${auditId}/questions/`);
    return response.data;
  },

  async submitAnswer(auditId, data) {
    const response = await api.post(`/audits/${auditId}/answer/`, data);
    return response.data;
  },

  async getResults(auditId) {
    const response = await api.get(`/audits/${auditId}/results/`);
    return response.data;
  },

  async getAnswers(auditId) {
    const response = await api.get(`/audits/${auditId}/answers/`);
    return response.data;
  },

  // Comparisons
  async compareAudits(auditIds) {
    const response = await api.get(`/comparisons/?audit_ids=${auditIds.join(',')}`);
    return response.data;
  },

  // Statistics
  async getStatistics() {
    const response = await api.get('/statistics/');
    return response.data;
  },

  async getDashboard() {
    const response = await api.get('/dashboard/');
    return response.data;
  },

  // Recommendations
  async getRecommendations(auditId) {
    const response = await api.get(`/recommendations/?audit=${auditId}`);
    return response.data;
  },

  async updateRecommendationStatus(id, status) {
    const response = await api.post(`/recommendations/${id}/update_status/`, { status });
    return response.data;
  },
};

export default auditService;
