import React from 'react';
import { useNavigate } from 'react-router-dom';
import { ClipboardCheck, Calendar, User, ArrowRight } from 'lucide-react';
import { Card } from '../common/Card';
import { StatusBadge } from '../common/Badge';
import { getScoreColor } from '../../utils/constants';

const AuditCard = ({ audit }) => {
  const navigate = useNavigate();

  return (
    <Card 
      className="hover:shadow-lg transition-all"
      onClick={() => navigate(`/audits/${audit.id}`)}
    >
      <div className="p-6">
        <div className="flex justify-between items-start mb-4">
          <div className="flex items-center gap-3">
            <div className="p-2 bg-primary-100 rounded-lg">
              <ClipboardCheck className="text-primary-600" size={24} />
            </div>
            <div>
              <h3 className="font-semibold text-gray-900">{audit.name}</h3>
              <p className="text-sm text-gray-500">{audit.code}</p>
            </div>
          </div>
          <StatusBadge status={audit.status} />
        </div>

        <div className="space-y-3 mb-4">
          <div className="flex items-center gap-2 text-sm text-gray-600">
            <Calendar size={16} />
            <span>{audit.level_name}</span>
          </div>
          {audit.assigned_to_name && (
            <div className="flex items-center gap-2 text-sm text-gray-600">
              <User size={16} />
              <span>{audit.assigned_to_name}</span>
            </div>
          )}
        </div>

        {/* Progress bar */}
        <div className="mb-4">
          <div className="flex justify-between text-sm mb-1">
            <span className="text-gray-600">Progreso</span>
            <span className="font-medium">{audit.progress_percentage}%</span>
          </div>
          <div className="w-full h-2 bg-gray-200 rounded-full overflow-hidden">
            <div 
              className="h-full bg-primary-600 rounded-full transition-all"
              style={{ width: `${audit.progress_percentage}%` }}
            />
          </div>
        </div>

        {/* Score for completed audits */}
        {audit.status === 'completed' && (
          <div className="flex justify-between items-center pt-4 border-t border-gray-100">
            <span className="text-sm text-gray-600">Puntuaci√≥n</span>
            <span className={`text-2xl font-bold ${getScoreColor(audit.score)}`}>
              {audit.score?.toFixed(1)}%
            </span>
          </div>
        )}

        <div className="flex justify-end mt-4">
          <button className="flex items-center gap-1 text-primary-600 hover:text-primary-700 text-sm font-medium">
            Ver detalles <ArrowRight size={16} />
          </button>
        </div>
      </div>
    </Card>
  );
};

export default AuditCard;
