import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { auditService } from '../../services/auditService';
import { Card, CardBody, CardHeader } from '../../components/common/Card';
import { LoadingPage } from '../../components/common/Loading';
import Button from '../../components/common/Button';
import QuestionCard from '../../components/audits/QuestionCard';
import { ArrowLeft, CheckCircle, ChevronLeft, ChevronRight } from 'lucide-react';

const ExecuteAuditPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [audit, setAudit] = useState(null);
  const [sections, setSections] = useState([]);
  const [currentSectionIndex, setCurrentSectionIndex] = useState(0);
  const [loading, setLoading] = useState(true);
  const [completing, setCompleting] = useState(false);

  useEffect(() => {
    loadAudit();
  }, [id]);

  const loadAudit = async () => {
    try {
      const [auditData, questionsData] = await Promise.all([
        auditService.getAudit(id),
        auditService.getQuestions(id),
      ]);
      setAudit(auditData);
      setSections(questionsData);
    } catch (err) {
      console.error('Error loading audit:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleAnswer = async (data) => {
    try {
      await auditService.submitAnswer(id, data);
      // Reload to update progress
      const auditData = await auditService.getAudit(id);
      setAudit(auditData);

      // Update local sections data
      setSections(prev => prev.map(section => ({
        ...section,
        questions: section.questions.map(q => 
          q.id === data.question_id 
            ? { ...q, answered: true, answer: data }
            : q
        )
      })));
    } catch (err) {
      console.error('Error submitting answer:', err);
    }
  };

  const handleComplete = async () => {
    if (!window.confirm('¿Estás seguro de completar la auditoría? No podrás modificar las respuestas.')) {
      return;
    }

    setCompleting(true);
    try {
      await auditService.completeAudit(id);
      navigate(`/audits/${id}`);
    } catch (err) {
      console.error('Error completing audit:', err);
      alert('Error al completar la auditoría');
    } finally {
      setCompleting(false);
    }
  };

  if (loading) return <LoadingPage />;
  if (!audit || sections.length === 0) return <div>Error cargando auditoría</div>;

  const currentSection = sections[currentSectionIndex];
  const totalAnswered = sections.reduce(
    (sum, s) => sum + s.questions.filter(q => q.answered).length, 0
  );
  const totalQuestions = sections.reduce((sum, s) => sum + s.questions.length, 0);
  const canComplete = totalAnswered === totalQuestions;

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div className="flex items-center gap-4">
          <button onClick={() => navigate(`/audits/${id}`)} className="p-2 hover:bg-gray-100 rounded-lg">
            <ArrowLeft size={20} />
          </button>
          <div>
            <h1 className="text-2xl font-bold text-gray-900">{audit.name}</h1>
            <p className="text-gray-500">{audit.code}</p>
          </div>
        </div>

        <Button 
          onClick={handleComplete} 
          disabled={!canComplete} 
          loading={completing}
          icon={CheckCircle}
          variant={canComplete ? 'success' : 'secondary'}
        >
          Completar Auditoría
        </Button>
      </div>

      {/* Progress */}
      <Card>
        <CardBody>
          <div className="flex items-center justify-between mb-2">
            <span className="text-gray-600">Progreso General</span>
            <span className="font-medium">{totalAnswered} / {totalQuestions}</span>
          </div>
          <div className="w-full h-3 bg-gray-200 rounded-full overflow-hidden">
            <div 
              className="h-full bg-primary-600 rounded-full transition-all"
              style={{ width: `${(totalAnswered / totalQuestions) * 100}%` }}
            />
          </div>
        </CardBody>
      </Card>

      {/* Section Navigation */}
      <div className="flex gap-2 overflow-x-auto pb-2">
        {sections.map((section, index) => {
          const answered = section.questions.filter(q => q.answered).length;
          const total = section.questions.length;
          const isComplete = answered === total;

          return (
            <button
              key={section.id}
              onClick={() => setCurrentSectionIndex(index)}
              className={`flex-shrink-0 px-4 py-2 rounded-lg text-sm font-medium transition-all ${
                index === currentSectionIndex
                  ? 'bg-primary-600 text-white'
                  : isComplete
                  ? 'bg-green-100 text-green-800'
                  : 'bg-gray-100 text-gray-600 hover:bg-gray-200'
              }`}
            >
              {section.code} ({answered}/{total})
            </button>
          );
        })}
      </div>

      {/* Current Section */}
      <Card>
        <CardHeader>
          <h2 className="text-lg font-semibold">{currentSection.name}</h2>
          {currentSection.description && (
            <p className="text-sm text-gray-500 mt-1">{currentSection.description}</p>
          )}
        </CardHeader>
        <CardBody className="space-y-6">
          {currentSection.questions.map((question) => (
            <QuestionCard
              key={question.id}
              question={question}
              answer={question.answer}
              onSubmit={handleAnswer}
            />
          ))}
        </CardBody>
      </Card>

      {/* Section Navigation Buttons */}
      <div className="flex justify-between">
        <Button
          variant="secondary"
          onClick={() => setCurrentSectionIndex(Math.max(0, currentSectionIndex - 1))}
          disabled={currentSectionIndex === 0}
          icon={ChevronLeft}
        >
          Sección Anterior
        </Button>
        <Button
          onClick={() => setCurrentSectionIndex(Math.min(sections.length - 1, currentSectionIndex + 1))}
          disabled={currentSectionIndex === sections.length - 1}
        >
          Siguiente Sección <ChevronRight size={20} />
        </Button>
      </div>
    </div>
  );
};

export default ExecuteAuditPage;
