import api from './api';

const auditService = {
  getTemplates: () => api.get('/audits/templates/'),

  getAudits: (params) => api.get('/audits/', { params }),

  getAudit: (id) => api.get('/audits/' + id + '/'),

  createAudit: (data) => api.post('/audits/', data),

  startAudit: (id) => api.post('/audits/' + id + '/start/'),

  completeAudit: (id) => api.post('/audits/' + id + '/complete/'),

  getQuestions: (auditId) => api.get('/audits/' + auditId + '/questions/'),

  submitAnswer: (auditId, questionId, data) => 
    api.post('/audits/' + auditId + '/answers/', {
      question_id: questionId,
      ...data
    }),

  getResults: (id) => api.get('/audits/' + id + '/results/'),

  compareAudits: (auditIds) => api.post('/audits/compare/', { audit_ids: auditIds }),

  getStatistics: () => api.get('/audits/statistics/'),

  getDashboard: () => api.get('/audits/dashboard/'),
};

export defaultÂ auditService;
