import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { auditService } from '../../services/auditService';
import { Card, CardBody } from '../../components/common/Card';
import { LoadingPage } from '../../components/common/Loading';
import Button from '../../components/common/Button';
import AuditCard from '../../components/audits/AuditCard';
import { Plus, Filter, Search } from 'lucide-react';

const AuditsListPage = () => {
  const navigate = useNavigate();
  const [audits, setAudits] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filter, setFilter] = useState('all');
  const [search, setSearch] = useState('');

  useEffect(() => {
    loadAudits();
  }, [filter]);

  const loadAudits = async () => {
    try {
      const params = filter !== 'all' ? { status: filter } : {};
      const result = await auditService.getAudits(params);
      setAudits(result.results || result);
    } catch (err) {
      console.error('Error loading audits:', err);
    } finally {
      setLoading(false);
    }
  };

  const filteredAudits = audits.filter(audit => 
    audit.name.toLowerCase().includes(search.toLowerCase()) ||
    audit.code.toLowerCase().includes(search.toLowerCase())
  );

  if (loading) return <LoadingPage />;

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">Auditorías</h1>
          <p className="text-gray-500">Gestiona tus auditorías ISO 27701</p>
        </div>
        <Button icon={Plus} onClick={() => navigate('/audits/new')}>
          Nueva Auditoría
        </Button>
      </div>

      {/* Filters */}
      <Card>
        <CardBody className="flex flex-wrap gap-4 items-center">
          <div className="relative flex-1 min-w-[200px]">
            <Search className="absolute left-3 top-1/2 -translate-y-1/2 text-gray-400" size={20} />
            <input
              type="text"
              value={search}
              onChange={(e) => setSearch(e.target.value)}
              placeholder="Buscar auditorías..."
              className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500"
            />
          </div>
          <div className="flex gap-2">
            {['all', 'draft', 'in_progress', 'completed'].map((status) => (
              <button
                key={status}
                onClick={() => setFilter(status)}
                className={`px-4 py-2 rounded-lg text-sm font-medium transition-colors ${
                  filter === status
                    ? 'bg-primary-600 text-white'
                    : 'bg-gray-100 text-gray-600 hover:bg-gray-200'
                }`}
              >
                {status === 'all' ? 'Todas' : 
                 status === 'draft' ? 'Borrador' :
                 status === 'in_progress' ? 'En Progreso' : 'Completadas'}
              </button>
            ))}
          </div>
        </CardBody>
      </Card>

      {/* Audits Grid */}
      {filteredAudits.length === 0 ? (
        <Card>
          <CardBody className="text-center py-12">
            <p className="text-gray-500">No hay auditorías que mostrar</p>
            <Button className="mt-4" onClick={() => navigate('/audits/new')}>
              Crear primera auditoría
            </Button>
          </CardBody>
        </Card>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
          {filteredAudits.map((audit) => (
            <AuditCard key={audit.id} audit={audit} />
          ))}
        </div>
      )}
    </div>
  );
};

export default AuditsListPage;
