import React, { useState } from 'react';
import { Check, X, MessageSquare } from 'lucide-react';
import Button from '../common/Button';

const QuestionCard = ({ question, answer, onSubmit, disabled = false }) => {
  const [value, setValue] = useState(
    answer?.answer_boolean ?? answer?.answer_scale ?? answer?.answer_choice ?? answer?.answer_text ?? null
  );
  const [comments, setComments] = useState(answer?.comments || '');
  const [showComments, setShowComments] = useState(!!answer?.comments);
  const [saving, setSaving] = useState(false);

  const handleSubmit = async () => {
    setSaving(true);
    try {
      const data = {
        question_id: question.id,
        comments: comments || null,
      };

      if (question.question_type === 'yes_no') {
        data.answer_boolean = value;
      } else if (question.question_type === 'scale') {
        data.answer_scale = value;
      } else if (question.question_type === 'multiple_choice') {
        data.answer_choice = value;
      } else if (question.question_type === 'text') {
        data.answer_text = value;
      }

      await onSubmit(data);
    } finally {
      setSaving(false);
    }
  };

  return (
    <div className={`bg-white rounded-xl border p-6 ${answer ? 'border-green-200 bg-green-50/30' : 'border-gray-200'}`}>
      <div className="flex items-start gap-4">
        <div className="flex-shrink-0 w-12 h-12 bg-primary-100 rounded-lg flex items-center justify-center">
          <span className="text-primary-600 font-bold text-sm">{question.code}</span>
        </div>

        <div className="flex-1">
          <p className="text-gray-900 font-medium mb-4">{question.text}</p>

          {question.description && (
            <p className="text-sm text-gray-500 mb-4">{question.description}</p>
          )}

          {/* Yes/No Question */}
          {question.question_type === 'yes_no' && (
            <div className="flex gap-4">
              <button
                onClick={() => setValue(true)}
                disabled={disabled}
                className={`flex-1 flex items-center justify-center gap-2 py-3 rounded-lg border-2 transition-all ${
                  value === true
                    ? 'border-green-500 bg-green-50 text-green-700'
                    : 'border-gray-200 hover:border-gray-300 text-gray-600'
                }`}
              >
                <Check size={20} />
                <span className="font-medium">SÃ­</span>
              </button>
              <button
                onClick={() => setValue(false)}
                disabled={disabled}
                className={`flex-1 flex items-center justify-center gap-2 py-3 rounded-lg border-2 transition-all ${
                  value === false
                    ? 'border-red-500 bg-red-50 text-red-700'
                    : 'border-gray-200 hover:border-gray-300 text-gray-600'
                }`}
              >
                <X size={20} />
                <span className="font-medium">No</span>
              </button>
            </div>
          )}

          {/* Scale Question */}
          {question.question_type === 'scale' && (
            <div className="flex gap-2">
              {[1, 2, 3, 4, 5].map((num) => (
                <button
                  key={num}
                  onClick={() => setValue(num)}
                  disabled={disabled}
                  className={`flex-1 py-3 rounded-lg border-2 font-medium transition-all ${
                    value === num
                      ? 'border-primary-500 bg-primary-50 text-primary-700'
                      : 'border-gray-200 hover:border-gray-300 text-gray-600'
                  }`}
                >
                  {num}
                </button>
              ))}
            </div>
          )}

          {/* Multiple Choice */}
          {question.question_type === 'multiple_choice' && question.choices && (
            <div className="space-y-2">
              {question.choices.map((choice) => (
                <button
                  key={choice}
                  onClick={() => setValue(choice)}
                  disabled={disabled}
                  className={`w-full text-left px-4 py-3 rounded-lg border-2 transition-all ${
                    value === choice
                      ? 'border-primary-500 bg-primary-50 text-primary-700'
                      : 'border-gray-200 hover:border-gray-300 text-gray-600'
                  }`}
                >
                  {choice}
                </button>
              ))}
            </div>
          )}

          {/* Text Question */}
          {question.question_type === 'text' && (
            <textarea
              value={value || ''}
              onChange={(e) => setValue(e.target.value)}
              disabled={disabled}
              className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent"
              rows={4}
              placeholder="Escribe tu respuesta..."
            />
          )}

          {/* Comments toggle */}
          <div className="mt-4">
            <button
              onClick={() => setShowComments(!showComments)}
              className="flex items-center gap-2 text-sm text-gray-500 hover:text-gray-700"
            >
              <MessageSquare size={16} />
              {showComments ? 'Ocultar comentarios' : 'Agregar comentarios'}
            </button>

            {showComments && (
              <textarea
                value={comments}
                onChange={(e) => setComments(e.target.value)}
                disabled={disabled}
                className="w-full mt-2 px-4 py-2 border border-gray-300 rounded-lg text-sm focus:ring-2 focus:ring-primary-500"
                rows={2}
                placeholder="Comentarios adicionales..."
              />
            )}
          </div>

          {/* Submit button */}
          {!disabled && value !== null && (
            <div className="mt-4 flex justify-end">
              <Button onClick={handleSubmit} loading={saving}>
                {answer ? 'Actualizar' : 'Guardar'} respuesta
              </Button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default QuestionCard;
