import React, { useState, useEffect } from 'react';
import { auditService } from '../../services/auditService';
import { Card, CardBody, CardHeader } from '../../components/common/Card';
import { LoadingPage } from '../../components/common/Loading';
import Button from '../../components/common/Button';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import { GitCompare, Check } from 'lucide-react';
import { getScoreColor } from '../../utils/constants';

const ComparisonsPage = () => {
  const [audits, setAudits] = useState([]);
  const [selected, setSelected] = useState([]);
  const [comparison, setComparison] = useState(null);
  const [loading, setLoading] = useState(true);
  const [comparing, setComparing] = useState(false);

  useEffect(() => { loadAudits(); }, []);

  const loadAudits = async () => {
    try {
      const result = await auditService.getAudits({ status: 'completed' });
      setAudits(result.results || result);
    } catch (err) { console.error(err); } finally { setLoading(false); }
  };

  const toggleSelect = (id) => {
    if (selected.includes(id)) setSelected(selected.filter(s => s !== id));
    else if (selected.length < 5) setSelected([...selected, id]);
  };

  const handleCompare = async () => {
    if (selected.length < 2) return alert('Selecciona al menos 2 auditorías');
    setComparing(true);
    try {
      const result = await auditService.compareAudits(selected);
      setComparison(result);
    } catch (err) { alert('Error al comparar'); } finally { setComparing(false); }
  };

  if (loading) return <LoadingPage />;

  const chartData = comparison?.sections_comparison?.map(section => {
    const data = { name: section.section_name?.substring(0, 15) || section.section_code };
    comparison.audits.forEach((audit, i) => { data[`audit_${i}`] = section.scores[i] || 0; });
    return data;
  }) || [];

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <div><h1 className="text-2xl font-bold text-gray-900">Comparaciones</h1><p className="text-gray-500">Compara resultados entre auditorías</p></div>
        <Button icon={GitCompare} onClick={handleCompare} loading={comparing} disabled={selected.length < 2}>Comparar ({selected.length})</Button>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        <Card>
          <CardHeader><h2 className="text-lg font-semibold">Seleccionar Auditorías (máx 5)</h2></CardHeader>
          <CardBody className="max-h-96 overflow-y-auto">
            {audits.length === 0 ? <p className="text-gray-500 text-center py-4">No hay auditorías completadas</p> : (
              <div className="space-y-2">
                {audits.map((audit) => (
                  <div key={audit.id} onClick={() => toggleSelect(audit.id)} className={`p-3 rounded-lg border-2 cursor-pointer transition-all ${selected.includes(audit.id) ? 'border-primary-500 bg-primary-50' : 'border-gray-200 hover:border-gray-300'}`}>
                    <div className="flex items-center justify-between">
                      <div>
                        <p className="font-medium">{audit.name}</p>
                        <p className="text-sm text-gray-500">{audit.code} • {audit.level_name}</p>
                      </div>
                      <div className="flex items-center gap-3">
                        <span className={`text-xl font-bold ${getScoreColor(audit.score)}`}>{audit.score?.toFixed(0)}%</span>
                        {selected.includes(audit.id) && <Check className="text-primary-600" size={20} />}
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </CardBody>
        </Card>

        {comparison && (
          <Card>
            <CardHeader><h2 className="text-lg font-semibold">Resultados</h2></CardHeader>
            <CardBody>
              <div className="space-y-4 mb-6">
                {comparison.audits.map((audit, i) => (
                  <div key={audit.id} className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                    <div className="flex items-center gap-2">
                      <div className={`w-4 h-4 rounded`} style={{ backgroundColor: ['#3b82f6', '#10b981', '#f59e0b', '#ef4444', '#8b5cf6'][i] }} />
                      <span className="font-medium">{audit.name}</span>
                    </div>
                    <span className={`text-lg font-bold ${getScoreColor(audit.score)}`}>{audit.score?.toFixed(1)}%</span>
                  </div>
                ))}
              </div>
              <p className="text-sm text-gray-500 mb-2">Mejor: <span className="font-medium text-green-600">{comparison.best_audit?.name}</span></p>
              <p className="text-sm text-gray-500">Peor: <span className="font-medium text-red-600">{comparison.worst_audit?.name}</span></p>
            </CardBody>
          </Card>
        )}
      </div>

      {comparison && chartData.length > 0 && (
        <Card>
          <CardHeader><h2 className="text-lg font-semibold">Comparación por Sección</h2></CardHeader>
          <CardBody>
            <ResponsiveContainer width="100%" height={400}>
              <BarChart data={chartData} margin={{ top: 20, right: 30, left: 20, bottom: 60 }}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="name" angle={-45} textAnchor="end" height={80} />
                <YAxis domain={[0, 100]} tickFormatter={(v) => `${v}%`} />
                <Tooltip formatter={(v) => `${v.toFixed(1)}%`} />
                <Legend />
                {comparison.audits.map((audit, i) => (
                  <Bar key={audit.id} dataKey={`audit_${i}`} name={audit.name} fill={['#3b82f6', '#10b981', '#f59e0b', '#ef4444', '#8b5cf6'][i]} />
                ))}
              </BarChart>
            </ResponsiveContainer>
          </CardBody>
        </Card>
      )}
    </div>
  );
};

export default ComparisonsPage;
